var quad = require('./quadratic');

exports.quadraticSolve = function(a,b,c){
    var tmp;
    
    quad(a,b,c, function(err,quadsolve) {
	if (err) {
                console.log('Error: ', err);
    		 }
    	else {
			    //console.log("Roots are "+ quadsolve.root1() + "  " + quadsolve.root2());
                
                tmp = quadsolve;    //quadsolve will contain 2 function properties 
                                    //root1() and root2()
    		 }
    });
    
    return tmp;
    
}